from pydantic import BaseModel, ConfigDictfrom datetime import datetimeclass BaseResponse(BaseModel):    """Базовая схема ответа"""    model_config = ConfigDict(from_attributes=True)    id: int    created_at: datetime    updated_at: datetimeclass PaginationParams(BaseModel):    """Параметры пагинации"""    skip: int = 0    limit: int = 100class MessageResponse(BaseModel):    """Схема для простых сообщений"""    message: str    success: bool = True